mapper.select:
select B.* from ( select ROWNUM RN, A.* from (select p.product_no, p.product_name, p.category, p.manufacturer_id, s.emp_id, p.enabled, p.reg_date, nvl(s.stock,0) stock, e.emp_name from product p left join stock s on (p.product_no = s.product_no) left join emp e on (s.emp_id = e.emp_id)
(s.emp_id = #{emp.empId} or s.emp_id is null)
and p.category = #{category}
and p.product_name like '%' || #{search} || '%'
and p.product_no = #{sNum}
and s.stock <= #{lNum}
and s.stock >= #{uNum}
order by p.product_name )A ) B WHERE RN BETWEEN #{page.start} and #{page.end}

select p.product_no product_no, p.product_name product_name, p.category category, p.manufacturer_id manufacturer_id, p.reg_date reg_date, s.stock stock, s.emp_id branchEmp from product p left join stock s on (p.product_no = s.product_no)
p.product_no = #{productNo}
and (s.emp_id = #{ eId } or s.emp_id is null)

select s.product_no, (select emp_name from emp where emp_id = s.emp_id) emp_name, s.emp_id, s.stock, p.product_name, p.category, (select emp_name from emp where emp_id = p.manufacturer_id) manufacturer_name from stock S inner join product P on S.product_no = P.product_no
s.emp_id = #{branchId}
and p.${searchType} like '%' || #{searchKeyword} || '%'

select rl.request_no, rl.manufacturer_id, rl.amount, TO_CHAR(rl.request_date,'YYYY-MM-DD') request_date, rl.confirm, rl.product_no, rl.emp_id, p.product_name, (select emp_name from emp where emp_id = rl.manufacturer_id) manufacturer_name, (select emp_name from emp where emp_id = rl.emp_id) branch_name from request_log rl inner join product p on rl.product_no = p.product_no
rl.emp_id = #{branchId}
rl.manufacturer_id = #{manufacturerId}
and rl.confirm = #{confirm}
and p.${searchType} like '%' || #{searchKeyword} || '%'
and rl.emp_id = #{branchId}

    select COUNT(*)
	from product p left join stock s
	on (p.product_no = s.product_no)


			(emp_id = #{emp.empId} or emp_id is null)


			 and p.category = #{category}



				and p.product_name like '%' || #{search} || '%'




				and p.product_no = #{sNum}



			and s.stock  <=  #{lNum}


			and s.stock  >=  #{uNum}
