mapper.select:
 	SELECT * 
		FROM (
			SELECT ROWNUM as rnum, B.*
				FROM (
                    select *
                        from(   
                            SELECT ba.*
                                 FROM cs ba ) 			


										and  member_id like  #{ memberId }


										and  secret  =  #{ secret }


                                 order by
						notice desc, cs_no desc
						) B
				)
		 where rnum BETWEEN #{ cPage } AND #{ numPerPage }
