mapper.select:
select 
    * 
from  (select r.* ,e.emp_name empName from emp e join board_reply r on e.emp_id = r.emp_id) 
where  board_no = #{ boardNo }
order by reg_date desc

mapper.update:
update board_reply 
	set
 content = #{ content }
 where board_reply_no = #{ boardReplyNo }

mapper.insert:
		insert into 
			board 
		values(seq_board_no.nextval 
		,#{ category }
		,#{ title }
		,#{ content } 
		,#{ empId}
		,default
		, 0)

			select
				seq_board_no.currval 			
 			from
 				dual

mapper.update:
update board 
	set read_count = read_count + 1
	where board_no = #{ no }

mapper.select:
SELECT * FROM ( SELECT ROWNUM as rnum, B.* FROM ( SELECT * FROM board
and ${ searchType } like '%' || #{ searchKeyword } || '%'
ORDER BY reg_date DESC ) B ) where rnum BETWEEN #{ cPage } AND #{ numPerPage }
