mapper.select:
select p.product_no,
	p.product_name,
	p.category,
	s.emp_id,
	p.reg_date,
	s.stock 
from product p left join stock s
on (p.product_no = s.product_no)


		p.category = #{category}



			and p.product_name like '%' || #{search} || '%'




			and p.product_no = #{sNum}



		and s.stock  <=  #{lNum}


		and s.stock  >=  #{uNum}
