mapper.select:
select B.* from ( select ROWNUM RN, A.* from (select p.product_no, p.product_name, p.category, s.emp_id, p.reg_date, nvl(s.stock,0) stock from product p left join stock s on (p.product_no = s.product_no )
emp_id = #{emp.empId} or emp_id is null
and p.category = #{category}
and p.product_name like '%' || #{search} || '%'
and p.product_no = #{sNum}
and s.stock <= #{lNum}
and s.stock >= #{uNum}
)A) B WHERE RN BETWEEN #{page.start} and #{page.end}

select p.product_no, p.product_name, p.category, p.manufacturer_id, p.reg_date, s.stock, s.emp_id branchEmp from product p left join stock s on (p.product_no = s.product_no)
p.product_no = #{productNo}
and s.emp_id = #{eId}

    select COUNT(*)
	from product p left join stock s
	on (p.product_no = s.product_no)


			emp_id = #{emp.empId} or emp_id is null


			 and p.category = #{category}



				and p.product_name like '%' || #{search} || '%'




				and p.product_no = #{sNum}



			and s.stock  <=  #{lNum}


