mapper.select:
			SELECT * 
			FROM (
			SELECT ROWNUM as rnum, M.*
				FROM (
			    	select *
                       from(   
                            SELECT mm.*
                                 FROM member mm
                             ) 					


								and ${ searchType }  like '%' || #{ searchKeyword } || '%'


						ORDER BY enroll_date DESC 
						) M
				)
			 where rnum BETWEEN #{ cPage } AND #{ numPerPage }
